import React from 'react';

function HomePage() {
  return (
    <div className="jumbotron d-flex justify-content-between align-items-center p-5" style={{ backgroundColor: '#f8f9fa' }}>
      <div className="text-left" style={{ flex: '1' }}>
        <h1 className="display-4 font-weight-bold">
          Discover the Golden Age of Cinema!
        </h1>
        <p className="lead">
          Explore a vast collection of old movies, comment on your favorites, like what you love, and follow fellow film enthusiasts!
        </p>
      </div>
      <div className="text-right" style={{ flex: '1' }}>
        <img
          src="your-image-url.jpg" // Replace with your image URL
          alt="Movies"
          className="img-fluid"
          style={{ maxWidth: '100%', height: 'auto' }}
        />
      </div>
    </div>
  );
}

export default HomePage;
