import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import spacer from '../assets/spacer.png';
import jumbotron from '../assets/jumbotron.jpg';
import api from '../api/axios'; // Adjust the path if needed
import { ThumbsUp, MessageCircle } from 'lucide-react';

function HomePage() {
  const [movies, setMovies] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchMovies = async () => {
      try {
        // Fetching only the specified movie IDs
        const response = await api.get('movies/?ids=25,80,150,400'); // Replace with your actual endpoint
        setMovies(response.data.results.slice(0, 5)); // Get only the first 5 results
      } catch (error) {
        console.error('Error fetching movies:', error);
      }
    };

    fetchMovies();
  }, []);

  return (
    <div>
      {/* Jumbotron Section */}
      <div className="jumbotron text-center" style={{
        position: 'relative',
        padding: '150px 0',
        background: `url(${jumbotron}) no-repeat center center / cover`
      }}>
        <h1 className="display-4 font-weight-bold text-white">
          Discover the Golden Age of Cinema!
        </h1>
        <p className="lead text-white">
          We got it all!
        </p>
      </div>

      {/* Trending Now Section */}
      <h2 className="text-center">Trending Now</h2>
      <div className="row row-cols-1 row-cols-sm-2 row-cols-md-3 row-cols-lg-4 g-4">
        {movies.map(movie => (
          <div key={movie.id} className="col">
            <div 
              className="card h-100" 
              onClick={() => navigate(`/movie/${movie.id}`)} // Navigate to movie details
            >
              <img 
                src={movie.thumbnail} 
                className="card-img-top" 
                alt={movie.title} 
                style={{ height: '200px', objectFit: 'cover' }} 
              />
              <div className="card-body">
                <h5 className="card-title text-center">{movie.title}</h5>
                <div className="d-flex justify-content-center gap-3">
                  <span className="d-flex align-items-center">
                    <ThumbsUp size={16} className="me-1" />
                    {movie.likes_count}
                  </span>
                  <span className="d-flex align-items-center">
                    <MessageCircle size={16} className="me-1" />
                    {movie.comments_count}
                  </span>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default HomePage;
