import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import api from '../../api/axios';
import { useAuth } from '../AuthContext/AuthContext';

const DEFAULT_AVATAR = 'https://res.cloudinary.com/dumvsoykz/image/upload/v1724754182/default_profile_yvdjcm.jpg';

function EditProfile() {
  const [profile, setProfile] = useState(null);
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [bio, setBio] = useState('');
  const [avatar, setAvatar] = useState(null);
  const [following, setFollowing] = useState([]);
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const { logout, user } = useAuth();
  const { username: profileUsername } = useParams();

  useEffect(() => {
    const loadProfileAndFollowing = async () => {
      try {
        const profileResponse = await api.get(profileUsername ? `profiles/${profileUsername}/` : 'profiles/me/');
        const profileData = profileResponse.data;
        setProfile(profileData);
        setUsername(profileData.username);
        setEmail(profileData.email);
        setBio(profileData.bio || '');
        await fetchFollowing(profileData.username);
      } catch (error) {
        console.error('Error loading profile and following:', error);
        setError('Failed to load profile and following list. Please try again.');
      }
    };

    loadProfileAndFollowing();
  }, [profileUsername]);

  const fetchFollowing = async (username) => {
    try {
      const response = await api.get(`profiles/${username}/following_list/`);
      setFollowing(response.data);
    } catch (error) {
      setError('Failed to fetch following list. Please try again.');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('username', username);
    formData.append('email', email);
    formData.append('bio', bio);
    if (avatar) {
      formData.append('avatar', avatar);
    }

    try {
      await api.put('profiles/me/', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      navigate('/profile');
    } catch (error) {
      console.error('Error updating profile:', error.response?.data || error);
    }
  };

  const handleDeleteAccount = async () => {
    if (window.confirm('Are you sure you want to delete your account? This action cannot be undone.')) {
      try {
        await api.delete('profiles/me/');
        logout();
        navigate('/home');
      } catch (error) {
        setError('Failed to delete account. Please try again.');
      }
    }
  };

  const handleUnfollow = async (profileId) => {
    try {
      await api.post(`profiles/${profileId}/follow/`);
      setFollowing((prevFollowing) => prevFollowing.filter((user) => user.profile_id !== profileId));
    } catch (error) {
      setError('Failed to unfollow user. Please try again.');
    }
  };

  const isOwnProfile = !profileUsername || profileUsername === user.username;

  return (
    <div className="max-w-4xl mx-auto p-4 flex flex-col md:flex-row">
      {/* Edit Profile Form */}
      <div className="w-full md:w-2/3 shadow-lg rounded-lg p-6">
        <h1 className="text-2xl font-bold mb-4">{isOwnProfile ? 'Profile' : `${profile.username}'s Profile`}</h1>
        {error && <div className="text-red-500 mb-4">{error}</div>}
        {isOwnProfile && (
          <form onSubmit={handleSubmit} className="mb-6">
            <div className="text-center mb-4">
              <img
                src={profile?.avatar || DEFAULT_AVATAR}
                alt={`${profile?.username}'s avatar`}
                style={{ width: '300px', height: '300px', marginRight: '5px' }}
              />
            </div>
            <div className="mb-4">
              <label htmlFor="username" className="block text-sm font-medium">Username:</label>
              <input
                type="text"
                id="username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50"
              />
            </div>
            <div className="mb-4">
              <label htmlFor="email" className="block text-sm font-medium">Email:</label>
              <input
                type="email"
                id="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50"
              />
            </div>
            <div className="mb-4">
              <label htmlFor="bio" className="block text-sm font-medium">Bio:</label>
              <textarea
                id="bio"
                value={bio}
                onChange={(e) => setBio(e.target.value)}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50"
              />
            </div>
            <div className="mb-4">
              <label htmlFor="avatar" className="block text-sm font-medium">Avatar:</label>
              <input
                type="file"
                onChange={(e) => setAvatar(e.target.files[0])}
                accept="image/*"
                className="mt-1 block w-full"
              />
            </div>
            <button type="submit" className="rounded">
              Update Profile
            </button>
          </form>
        )}
      </div>

      {/* Following List */}
      <div className=" md:w-1/3 shadow-lg rounded-lg p-6 ml-0 md:ml-6 flex flex-col">
        <h2 className="text-xl font-semibold mb-4">Following</h2>
        {following.length > 0 ? (
          <ul className="space-y-4">
            {following.map((followedUser) => (
              <li key={followedUser.profile_id} className="flex items-center justify-between">
                <div className="flex items-center">
                  <img
                    src={followedUser.avatar || DEFAULT_AVATAR}
                    alt={`${followedUser.username}'s avatar`}
                    style={{ width: '100px', height: '100px', borderRadius: '50%', marginRight: '5px' }} 
                  />
                  <span>{followedUser.username}</span>
                </div>
                {isOwnProfile && (
                  <button
                    onClick={() => handleUnfollow(followedUser.profile_id)}
                    className="bg-red-500 hover:bg-red-600 py-1 px-3 rounded text-sm"
                  >
                    Unfollow
                  </button>
                )}
              </li>
            ))}
          </ul>
        ) : (
          <p>Not following anyone yet.</p>
        )}
        {isOwnProfile && (
          <button
            onClick={handleDeleteAccount}
            className="rounded"
          >
            Delete Account
          </button>
        )}
      </div>
    </div>
  );
}

export default EditProfile;
