import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const ProfilePage = () => {
  const [profile, setProfile] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [bio, setBio] = useState('');
  const [avatar, setAvatar] = useState(null);
  const [followers, setFollowers] = useState([]);
  const [following, setFollowing] = useState([]);
  const [showFollowers, setShowFollowers] = useState(false);
  const [showFollowing, setShowFollowing] = useState(false);
  const { username } = useParams();
  const currentUser = JSON.parse(localStorage.getItem('user'));

  useEffect(() => {
    fetchProfile();
  }, [username]);

  const fetchProfile = async () => {
    try {
      const response = await axios.get(`/api/profiles/${username}/`);
      setProfile(response.data);
      setBio(response.data.bio);
    } catch (error) {
      console.error('Error fetching profile:', error);
    }
  };

  const handleFollow = async () => {
    try {
      await axios.post(`/api/profiles/${profile.id}/follow/`);
      fetchProfile();
    } catch (error) {
      console.error('Error following/unfollowing:', error);
    }
  };

  const handleEditProfile = async () => {
    try {
      const formData = new FormData();
      formData.append('bio', bio);
      if (avatar) {
        formData.append('avatar', avatar);
      }

      await axios.patch(`/api/profiles/${profile.id}/`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      setIsEditing(false);
      fetchProfile();
    } catch (error) {
      console.error('Error updating profile:', error);
    }
  };

  const fetchFollowers = async () => {
    try {
      const response = await axios.get(`/api/profiles/${profile.id}/followers/`);
      setFollowers(response.data);
      setShowFollowers(true);
    } catch (error) {
      console.error('Error fetching followers:', error);
    }
  };

  const fetchFollowing = async () => {
    try {
      const response = await axios.get(`/api/profiles/${profile.id}/following/`);
      setFollowing(response.data);
      setShowFollowing(true);
    } catch (error) {
      console.error('Error fetching following:', error);
    }
  };

  if (!profile) return <div>Loading...</div>;

  return (
    <div className="container mx-auto p-4">
      <div className="bg-white shadow rounded-lg p-6">
        <div className="flex items-center space-x-6 mb-4">
          <img
            className="h-24 w-24 rounded-full"
            src={profile.avatar}
            alt={`${profile.username}'s avatar`}
          />
          <div>
            <h2 className="text-2xl font-bold">{profile.username}</h2>
            {isEditing ? (
              <textarea
                value={bio}
                onChange={(e) => setBio(e.target.value)}
                className="mt-2 p-2 border rounded"
              />
            ) : (
              <p className="text-gray-700 mt-2">{profile.bio}</p>
            )}
          </div>
        </div>

        <div className="flex space-x-4 mb-4">
          <button onClick={fetchFollowers} className="text-blue-500 hover:underline">
            Followers: {profile.follower_count}
          </button>
          <button onClick={fetchFollowing} className="text-blue-500 hover:underline">
            Following: {profile.following_count}
          </button>
          <span>Total Likes: {profile.total_likes_received}</span>
        </div>

        {currentUser && currentUser.username !== profile.username && (
          <button
            onClick={handleFollow}
            className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded"
          >
            {profile.is_following ? 'Unfollow' : 'Follow'}
          </button>
        )}

        {currentUser && currentUser.username === profile.username && (
          <div>
            {isEditing ? (
              <div>
                <input
                  type="file"
                  onChange={(e) => setAvatar(e.target.files[0])}
                  className="mb-2"
                />
                <button
                  onClick={handleEditProfile}
                  className="bg-green-500 hover:bg-green-600 text-white font-bold py-2 px-4 rounded mr-2"
                >
                  Save Changes
                </button>
                <button
                  onClick={() => setIsEditing(false)}
                  className="bg-red-500 hover:bg-red-600 text-white font-bold py-2 px-4 rounded"
                >
                  Cancel
                </button>
              </div>
            ) : (
              <button
                onClick={() => setIsEditing(true)}
                className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded"
              >
                Edit Profile
              </button>
            )}
          </div>
        )}

        {showFollowers && (
          <div className="mt-4">
            <h3 className="text-xl font-bold mb-2">Followers</h3>
            <ul>
              {followers.map((follower) => (
                <li key={follower.id}>{follower.username}</li>
              ))}
            </ul>
            <button
              onClick={() => setShowFollowers(false)}
              className="mt-2 text-blue-500 hover:underline"
            >
              Close
            </button>
          </div>
        )}

        {showFollowing && (
          <div className="mt-4">
            <h3 className="text-xl font-bold mb-2">Following</h3>
            <ul>
              {following.map((followed) => (
                <li key={followed.id}>{followed.username}</li>
              ))}
            </ul>
            <button
              onClick={() => setShowFollowing(false)}
              className="mt-2 text-blue-500 hover:underline"
            >
              Close
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default ProfilePage;
