import React, { useState, useEffect } from 'react';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../AuthContext/AuthContext';
import api from '../../api/axios';
import './NavBar.css';
import zaptalklogo from '../../assets/zaptalklogo.webp';

function NavBar() {
  const { isLoggedIn, logout } = useAuth();
  const navigate = useNavigate();
  const [userProfile, setUserProfile] = useState(null);

  useEffect(() => {
    if (isLoggedIn) {
      fetchUserProfile();
    }
  }, [isLoggedIn]);

  const fetchUserProfile = async () => {
    try {
      const response = await api.get('profiles/me/', {
        headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
      });
      setUserProfile({
        ...response.data,
        is_superuser: response.data.is_superuser,
        is_banned: response.data.is_banned
      });
    } catch (error) {
      console.error('Error fetching user profile:', error);
    }
  };

  const handleLogout = () => {
    logout();
    navigate('/home');
  };

  return (
    <Navbar bg="dark" variant="dark" expand="lg">
      <Container>
        <Navbar.Brand as={Link} to="/home">
          <img
            src={zaptalklogo}
            width="30"
            height="30"
            className="d-inline-block align-top"
            alt="ZapTalk logo"
          />
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">
            <Nav.Link as={Link} to="/movies">Movies</Nav.Link>
            <Nav.Link as={Link} to="/profiles">Profiles</Nav.Link>
            <Nav.Link as={Link} to="/feed">Feed</Nav.Link>
            {userProfile?.is_superuser && (
              <Nav.Link as={Link} to="/bans">Bans</Nav.Link>
            )}
          </Nav>
          <Nav>
            {isLoggedIn ? (
              <NavDropdown 
                title={
                  <span>
                    <img 
                      src={userProfile?.avatar || '/default-avatar.png'} 
                      alt="User Avatar" 
                      style={{ width: '30px', height: '30px', borderRadius: '50%', marginRight: '10px' }}
                    />
                    Profile
                  </span>
                } 
                id="basic-nav-dropdown"
                align="end"
              >
                {userProfile?.is_banned ? (
                  <NavDropdown.Item as={Link} to="/ban-appeal">Ban Appeal</NavDropdown.Item>
                ) : (
                  <NavDropdown.Item as={Link} to="/edit-profile">Edit Profile</NavDropdown.Item>
                )}
                <NavDropdown.Divider />
                <NavDropdown.Item onClick={handleLogout}>Logout</NavDropdown.Item>
              </NavDropdown>
            ) : (
              <Nav.Link as={Link} to="/login">Login</Nav.Link>
            )}
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default NavBar;
