import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { Container, Row, Col, Image, Button, Form } from 'react-bootstrap';

const ProfilePage = () => {
  const [profile, setProfile] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [bio, setBio] = useState('');
  const [avatar, setAvatar] = useState(null);
  const [followers, setFollowers] = useState([]);
  const [following, setFollowing] = useState([]);
  const [showFollowers, setShowFollowers] = useState(false);
  const [showFollowing, setShowFollowing] = useState(false);
  const { username } = useParams();
  const currentUser = JSON.parse(localStorage.getItem('user'));

  useEffect(() => {
    fetchProfile();
  }, [username]);

  const fetchProfile = async () => {
    try {
      console.log('Fetching profile for:', username);
      const response = await axios.get(`/api/profiles/${username}/`);
      console.log('Profile data received:', response.data);
      setProfile(response.data);
      setBio(response.data.bio);
    } catch (error) {
      console.error('Error fetching profile:', error);
    }
  };

  const handleFollow = async () => {
    try {
      await axios.post(`/api/profiles/${profile.id}/follow/`);
      fetchProfile();
    } catch (error) {
      console.error('Error following/unfollowing:', error);
    }
  };

  const handleEditProfile = async () => {
    try {
      const formData = new FormData();
      formData.append('bio', bio);
      if (avatar) {
        formData.append('avatar', avatar);
      }

      await axios.patch(`/api/profiles/${profile.id}/`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      setIsEditing(false);
      fetchProfile();
    } catch (error) {
      console.error('Error updating profile:', error);
    }
  };

  const fetchFollowers = async () => {
    try {
      const response = await axios.get(`/api/profiles/${profile.id}/followers/`);
      setFollowers(response.data);
      setShowFollowers(true);
    } catch (error) {
      console.error('Error fetching followers:', error);
    }
  };

  const fetchFollowing = async () => {
    try {
      const response = await axios.get(`/api/profiles/${profile.id}/following/`);
      setFollowing(response.data);
      setShowFollowing(true);
    } catch (error) {
      console.error('Error fetching following:', error);
    }
  };

  console.log('Current profile state:', profile);

  if (!profile) return <div>Loading...</div>;

  return (
    <Container>
      <Row className="mt-4">
        <Col md={4}>
          <Image src={profile.avatar} roundedCircle fluid />
        </Col>
        <Col md={8}>
          <h2>{profile.username}</h2>
          {isEditing ? (
            <Form.Control
              as="textarea"
              value={bio}
              onChange={(e) => setBio(e.target.value)}
            />
          ) : (
            <p>{profile.bio}</p>
          )}
          <div className="mt-3">
            <Button variant="link" onClick={fetchFollowers}>
              Followers: {profile.follower_count}
            </Button>
            <Button variant="link" onClick={fetchFollowing}>
              Following: {profile.following_count}
            </Button>
            <span>Total Likes: {profile.total_likes_received}</span>
          </div>
          {currentUser && currentUser.username !== profile.username && (
            <Button variant="primary" onClick={handleFollow}>
              {profile.is_following ? 'Unfollow' : 'Follow'}
            </Button>
          )}
          {currentUser && currentUser.username === profile.username && (
            <>
              {isEditing ? (
                <>
                  <Form.Control
                    type="file"
                    onChange={(e) => setAvatar(e.target.files[0])}
                    className="mt-2"
                  />
                  <Button variant="success" onClick={handleEditProfile} className="mt-2 mr-2">
                    Save Changes
                  </Button>
                  <Button variant="danger" onClick={() => setIsEditing(false)} className="mt-2">
                    Cancel
                  </Button>
                </>
              ) : (
                <Button variant="primary" onClick={() => setIsEditing(true)} className="mt-2">
                  Edit Profile
                </Button>
              )}
            </>
          )}
        </Col>
      </Row>
      {showFollowers && (
        <Row className="mt-4">
          <Col>
            <h3>Followers</h3>
            <ul>
              {followers.map((follower) => (
                <li key={follower.id}>{follower.username}</li>
              ))}
            </ul>
            <Button variant="link" onClick={() => setShowFollowers(false)}>
              Close
            </Button>
          </Col>
        </Row>
      )}
      {showFollowing && (
        <Row className="mt-4">
          <Col>
            <h3>Following</h3>
            <ul>
              {following.map((followed) => (
                <li key={followed.id}>{followed.username}</li>
              ))}
            </ul>
            <Button variant="link" onClick={() => setShowFollowing(false)}>
              Close
            </Button>
          </Col>
        </Row>
      )}
    </Container>
  );
};

export default ProfilePage;
