import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import api from '../../api/axios';
import { format } from 'date-fns';

function FeedPage() {
  const [feedItems, setFeedItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchFeedItems = async () => {
      try {
        const [commentsResponse, likesResponse] = await Promise.all([
          api.get('comments/'),
          api.get('likes/')
        ]);

        const comments = commentsResponse.data.map(comment => ({
          ...comment,
          type: 'comment'
        }));

        const likes = likesResponse.data.map(like => ({
          ...like,
          type: 'like'
        }));

        const combinedFeed = [...comments, ...likes].sort((a, b) => 
          new Date(b.created_at) - new Date(a.created_at)
        );

        setFeedItems(combinedFeed);
        setLoading(false);
      } catch (err) {
        console.error('Error details:', err.response || err);
        setError('Failed to fetch feed items: ' + (err.response?.data?.detail || err.message));
        setLoading(false);
      }
    };

    fetchFeedItems();
  }, []);

  const renderFeedItem = (item) => {
    if (item.type === 'comment') {
      return (
        <div key={`comment-${item.id}`} className="bg-white p-4 mb-4 rounded-lg shadow">
          <Link to={`/profile/${item.user.username}`} className="font-bold text-blue-600 hover:underline">
            {item.user.username}
          </Link>
          <span> commented on </span>
          <Link to={`/movie/${item.movie}`} className="font-bold text-blue-600 hover:underline">
            {item.movie_title || `Movie ID: ${item.movie}`}
          </Link>:
          <p className="mt-2">{item.content}</p>
          <p className="text-sm text-gray-500 mt-2">
            {format(new Date(item.created_at), 'PPpp')}
          </p>
        </div>
      );
    } else if (item.type === 'like') {
      return (
        <div key={`like-${item.id}`} className="bg-white p-4 mb-4 rounded-lg shadow">
          <Link to={`/profile/${item.user.username}`} className="font-bold text-blue-600 hover:underline">
            {item.user.username}
          </Link>
          <span> liked </span>
          {item.content_type === 'movie' ? (
            <Link to={`/movie/${item.object_id}`} className="font-bold text-blue-600 hover:underline">
              {item.movie_title || `Movie ID: ${item.object_id}`}
            </Link>
          ) : (
            <span>
              a comment on <Link to={`/movie/${item.content_object.movie_id}`} className="font-bold text-blue-600 hover:underline">
                {item.movie_title || `Movie ID: ${item.content_object.movie_id}`}
              </Link>: "{item.content_object.content}"
            </span>
          )}
          <p className="text-sm text-gray-500 mt-2">
            {format(new Date(item.created_at), 'PPpp')}
          </p>
        </div>
      );
    }
  };

  if (loading) return <div className="text-center p-4">Loading...</div>;
  if (error) return <div className="text-center p-4 text-red-500">{error}</div>;

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-6">Feed</h1>
      {feedItems.length === 0 ? (
        <p>No feed items to display.</p>
      ) : (
        feedItems.map(renderFeedItem)
      )}
    </div>
  );
}

export default FeedPage;
