import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import api from '../../api/axios';
import { useAuth } from '../AuthContext/AuthContext';

function EditProfile() {
  const [profile, setProfile] = useState(null);
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [bio, setBio] = useState('');
  const [avatar, setAvatar] = useState(null);
  const [following, setFollowing] = useState([]);
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const { logout, user } = useAuth();
  const { username: profileUsername } = useParams();

  useEffect(() => {
    fetchProfile();
  }, [profileUsername]);

  useEffect(() => {
    if (profile) {
      fetchFollowing();
    }
  }, [profile]);

  const fetchProfile = async () => {
    try {
      const response = await api.get(profileUsername ? `api/profiles/${profileUsername}/` : 'api/profiles/me/', {
        headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
      });
      const profileData = response.data;
      setProfile(profileData);
      setUsername(profileData.username);
      setEmail(profileData.email);
      setBio(profileData.bio || '');
    } catch (error) {
      console.error('Error fetching profile:', error);
      setError('Failed to fetch profile. Please try again.');
    }
  };

  const fetchFollowing = async () => {
    try {
      const response = await api.get(`api/profiles/${profile.username}/following_list/`, {
        headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
      });
      setFollowing(response.data);
    } catch (error) {
      console.error('Error fetching following:', error);
    }
  };

  const handleUnfollow = async (profileId) => {
    try {
      await api.post(`api/profiles/${profileId}/follow/`, {}, {
        headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
      });
      fetchFollowing();  // Refresh the following list
    } catch (error) {
      console.error('Error unfollowing user:', error);
    }
  };

  const handleSubmit = async (e) => {
    // ... (your existing handleSubmit code)
  };

  const handleDeleteAccount = async () => {
    // ... (your existing handleDeleteAccount code)
  };

  if (!profile) {
    return <div>Loading...</div>;
  }

  const isOwnProfile = user.username === profile.username;

  return (
    <div className="edit-profile">
      <h1>{isOwnProfile ? 'Edit Profile' : `${profile.username}'s Profile`}</h1>
      {error && <div className="error-message">{error}</div>}

      {isOwnProfile && (
        <form onSubmit={handleSubmit}>
          {/* ... (your existing form fields) */}
          <button type="submit">Update Profile</button>
        </form>
      )}

      <h2>Following ({following.length})</h2>
      <ul>
        {following.map(followedUser => (
          <li key={followedUser.user_id}>
            {followedUser.username}
            {isOwnProfile && (
              <button onClick={() => handleUnfollow(followedUser.profile_id)}>Unfollow</button>
            )}
          </li>
        ))}
      </ul>

      {isOwnProfile && (
        <button onClick={handleDeleteAccount} className="delete-account">Delete Account</button>
      )}
    </div>
  );
}

export default EditProfile;
