import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import api from '../../api/axios';

const DEFAULT_AVATAR = 'https://res.cloudinary.com/dumvsoykz/image/upload/v1724754182/default_profile_yvdjcm.jpg';

function MovieDetail() {
  // ... (keep all your existing state and useEffect logic)

  return (
    <div className="container-fluid p-0">
      <div className="position-relative">
        <img src={movie.thumbnail} alt={movie.title} className="img-fluid w-100" style={{ maxWidth: '95%', margin: '0 auto', display: 'block' }} />
        <div className="position-absolute top-0 start-0 h-100 d-flex flex-column justify-content-center text-white p-4" style={{ width: '30%', background: 'linear-gradient(to right, #181818 0%, rgba(24, 24, 24, 0) 100%)' }}>
          <h1 className="display-4 fw-bold">{movie.title}</h1>
          <p className="fs-5">{movie.year}</p>
          <p className="fs-6 text-light">{movie.genres.join(', ')}</p>
          <p className="mt-3">{movie.extract}</p>
        </div>
      </div>

      <div className="d-flex justify-content-center align-items-center my-4">
        <p className="me-4">Likes: {movie.likes_count}</p>
        <button onClick={handleLike} className="btn btn-primary">Like</button>
      </div>

      <div className="comments-section mt-5">
        <h2 className="text-center mb-4">Comments</h2>
        {isLoggedIn ? (
          <>
            {comments.length > 0 ? (
              comments.map(comment => (
                <div key={comment.id} className="comment mb-4">
                  {editingComment && editingComment.id === comment.id ? (
                    <div>
                      <textarea
                        value={editedContent}
                        onChange={(e) => setEditedContent(e.target.value)}
                        className="form-control mb-2"
                      />
                      <button onClick={handleUpdateComment} className="btn btn-primary btn-sm me-2">Save</button>
                      <button onClick={() => setEditingComment(null)} className="btn btn-secondary btn-sm">Cancel</button>
                    </div>
                  ) : (
                    <div className="d-flex">
                      <div className="me-3 text-center">
                        <Link to={`/profile/${comment.user.username}`}>
                          <img 
                            src={comment.user.avatar || DEFAULT_AVATAR}
                            alt={`${comment.user.username}'s avatar`} 
                            className="rounded-circle mb-2"
                            style={{width: '50px', height: '50px', objectFit: 'cover'}}
                            onError={(e) => {
                              console.error(`Error loading avatar for ${comment.user?.username}:`, e);
                              e.target.src = DEFAULT_AVATAR;
                            }}
                          />
                        </Link>
                        <div className="small">{comment.user.username}</div>
                        <div className="small text-muted">{new Date(comment.created_at).toLocaleDateString()}</div>
                      </div>
                      <div className="flex-grow-1">
                        <p>{comment.content}</p>
                        <div className="d-flex justify-content-between align-items-center">
                          <div>
                            {currentUserProfile && currentUserProfile.username !== comment.user.username && (
                              <button onClick={() => handleLikeComment(comment.id)} className="btn btn-link btn-sm p-0 me-2">
                                {comment.is_liked_by_user ? 'Unlike' : 'Like'}
                              </button>
                            )}
                            <span className="small text-muted">{comment.likes_count} likes</span>
                          </div>
                          {currentUserProfile && currentUserProfile.username === comment.user.username && (
                            <div>
                              <button onClick={() => handleEditComment(comment)} className="btn btn-link btn-sm p-0 me-2">Edit</button>
                              <button onClick={() => handleDeleteComment(comment.id)} className="btn btn-link btn-sm p-0 text-danger">Delete</button>
                            </div>
                          )}
                        </div>
                      </div>
                    </div>
                  )}
                </div>
              ))
            ) : (
              <p className="text-center">No comments yet.</p>
            )}

            <form onSubmit={handleCommentSubmit} className="mt-4">
              <textarea
                value={newComment}
                onChange={(e) => setNewComment(e.target.value)}
                placeholder="Write a comment..."
                required
                className="form-control mb-2"
              />
              <button type="submit" className="btn btn-primary d-block mx-auto">Post Comment</button>
            </form>
          </>
        ) : (
          <p className="text-center">Please log in to view and post comments.</p>
        )}
      </div>
      {error && <div className="alert alert-danger mt-3">{error}</div>}
    </div>
  );
}

export default MovieDetail;
