import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../components/AuthContext/AuthContext';
import './LoginPage.css';

const LoginPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const { isLoggedIn, login, logout } = useAuth();
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    if (!username || !password) {
      setError('Please enter both username and password');
      return;
    }

    try {
      const response = await axios.post('https://zaptalk-api-c46804cb19e0.herokuapp.com/api/token/', {
        username,
        password
      }, {
        headers: {
          'Content-Type': 'application/json'
        }
      });

      if (response.data && response.data.access) {
        login(response.data.access);
        navigate('/home');
      } else {
        setError('Invalid response from server');
      }
    } catch (error) {
      console.error('Login failed:', error);
      setError(`Login failed: ${error.response?.data?.detail || 'Unknown error'}`);
    }
  };

  const handleLogout = () => {
    logout();
    navigate('/home');
  };

  if (isLoggedIn) {
    return (
      <div className="login-card-container">
        <div className="login-card">
          <h2>You are logged in</h2>
          <button onClick={handleLogout}>Logout</button>
        </div>
      </div>
    );
  }

  return (
    <div className="login-card-container">
      <div className="login-card">
        <div className="login-card-logo">
          <img src="/logo.png" alt="logo" />
        </div>
        <div className="login-card-header">
          <h1>Login</h1>
          <div>Please login to use the platform</div>
        </div>
        {error && <p style={{ color: 'red' }}>{error}</p>}
        <form className="login-card-form" onSubmit={handleSubmit}>
          <div className="form-item">
            <span className="form-item-icon material-symbols-rounded">person</span>
            <input
              type="text"
              placeholder="Enter Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
          </div>
          <div className="form-item">
            <span className="form-item-icon material-symbols-rounded">lock</span>
            <input
              type="password"
              placeholder="Enter Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <button type="submit">Login</button>
        </form>
        <div className="login-card-footer">
          <p>
            Has your account been banned? <Link to="/ban-appeal">Click here to submit a ban appeal</Link>
          </p>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
