import React, { useState, useEffect, useCallback } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import api from '../../api/axios';
import './MovieList.css';

function MovieList() {
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [page, setPage] = useState(1);
  const [hasMore, setHasMore] = useState(true);
  const [genres, setGenres] = useState([]);
  const [selectedGenres, setSelectedGenres] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortBy, setSortBy] = useState('');
  const [showFollowedLikes, setShowFollowedLikes] = useState(false);
  const navigate = useNavigate();

  const fetchMovies = useCallback(async (resetMovies = false) => {
    setLoading(true);
    try {
      const response = await api.get('movies/', {
        params: {
          page: resetMovies ? 1 : page,
          genres: selectedGenres.join(','),
          search: searchTerm,
          sort: sortBy,
          followed_likes: showFollowedLikes
        }
      });
      const newMovies = response.data.results;
      setMovies(prevMovies => resetMovies ? newMovies : [...prevMovies, ...newMovies]);
      setHasMore(response.data.next !== null);
      setPage(prevPage => resetMovies ? 1 : prevPage + 1);
      setError(null);
    } catch (error) {
      console.error('Error fetching movies:', error);
      if (error.response && error.response.status === 401) {
        setError('You have to be logged in to see movies.');
      } else {
        setError('An error occurred while fetching movies. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  }, [page, selectedGenres, searchTerm, sortBy, showFollowedLikes]);

  useEffect(() => {
    fetchMovies(true);
  }, [selectedGenres, searchTerm, sortBy, showFollowedLikes, fetchMovies]);

  useEffect(() => {
    const fetchGenres = async () => {
      try {
        const response = await api.get('genres/');
        setGenres(response.data);
      } catch (error) {
        console.error('Error fetching genres:', error);
      }
    };
    fetchGenres();
  }, []);

  const handleGenreChange = (genre) => {
    setSelectedGenres(prev => 
      prev.includes(genre) ? prev.filter(g => g !== genre) : [...prev, genre]
    );
  };

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleSortChange = (event) => {
    setSortBy(event.target.value);
  };

  const toggleFollowedLikes = () => {
    setShowFollowedLikes(prev => !prev);
  };

  const handleLoadMore = () => {
    if (!loading && hasMore) {
      fetchMovies();
    }
  };

  const handleMovieClick = (movieId) => {
    navigate(`/movie/${movieId}`);
  };

  return (
    <div>
      <h1>Movies</h1>
      <div className="filters">
        <input 
          type="text" 
          placeholder="Search movies..." 
          value={searchTerm} 
          onChange={handleSearchChange}
        />
        <select onChange={handleSortChange} value={sortBy}>
          <option value="">Sort by...</option>
          <option value="most_liked">Most Liked</option>
          <option value="most_commented">Most Commented</option>
        </select>
        <button onClick={toggleFollowedLikes}>
          {showFollowedLikes ? 'Show All' : 'Show Liked by Followed Users'}
        </button>
        <div>
          {genres.map(genre => (
            <label key={genre}>
              <input
                type="checkbox"
                checked={selectedGenres.includes(genre)}
                onChange={() => handleGenreChange(genre)}
              />
              {genre}
            </label>
          ))}
        </div>
      </div>
      {error && <div className="error-message">{error}</div>}
      <div className="movie-grid">
        {movies.map((movie) => (
          <div key={movie.id} className="movie-card" onClick={() => handleMovieClick(movie.id)}>
            <img src={movie.thumbnail} alt={movie.title} />
            <h3>{movie.title}</h3>
            {movie.likes_count !== undefined && <p>Likes: {movie.likes_count}</p>}
          </div>
        ))}
      </div>
      {loading && <div>Loading...</div>}
      {!loading && hasMore && (
        <button onClick={handleLoadMore}>Load More</button>
      )}
      {!loading && !hasMore && movies.length === 0 && (
        <div>No movies found matching your criteria.</div>
      )}
    </div>
  );
}

export default MovieList;
