import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { ThumbsUp, MessageCircle } from 'lucide-react';

const MovieDetail = ({ movie, comments, isLoggedIn, currentUserProfile, handleLike, handleCommentSubmit, handleLikeComment, handleEditComment, handleDeleteComment, handleUpdateComment }) => {
  const [newComment, setNewComment] = useState('');
  const [editingComment, setEditingComment] = useState(null);
  const [editedContent, setEditedContent] = useState('');
  const [error, setError] = useState(null);

  const DEFAULT_AVATAR = '/path/to/default/avatar.png';

  return (
    <div className="movie-detail container-fluid p-0">
      <div className="position-relative">
        <img src={movie.thumbnail} alt={movie.title} className="img-fluid w-100" style={{maxWidth: '95%', margin: '0 auto', display: 'block'}} />
        <div className="movie-info-overlay position-absolute top-0 start-0 h-100 d-flex flex-column justify-content-center text-white p-4">
          <h1 className="display-4 fw-bold">{movie.title}</h1>
          <p className="fs-5">{movie.year}</p>
          <p className="fs-6 text-light">{movie.genres.join(', ')}</p>
          <p className="mt-3">{movie.extract}</p>
        </div>
      </div>

      <div className="d-flex justify-content-center align-items-center my-4">
        <div className="me-4">
          <ThumbsUp size={24} />
          <span className="ms-2">{movie.likes_count}</span>
        </div>
        <div>
          <MessageCircle size={24} />
          <span className="ms-2">{comments.length}</span>
        </div>
      </div>

      <div className="comments-section mt-5">
        <h2 className="mb-4">Comments</h2>
        {isLoggedIn ? (
          <>
            {comments.length > 0 ? (
              comments.map(comment => (
                <div key={comment.id} className="comment mb-4 d-flex">
                  <div className="me-3 text-center">
                    <Link to={`/profile/${comment.user.username}`}>
                      <img 
                        src={comment.user.avatar || DEFAULT_AVATAR}
                        alt={`${comment.user.username}'s avatar`} 
                        className="rounded-circle mb-2"
                        style={{width: '50px', height: '50px', objectFit: 'cover'}}
                        onError={(e) => {
                          console.error(`Error loading avatar for ${comment.user?.username}:`, e);
                          e.target.src = DEFAULT_AVATAR;
                        }}
                      />
                    </Link>
                    <div className="small">{comment.user.username}</div>
                    <div className="small text-muted">{new Date(comment.created_at).toLocaleDateString()}</div>
                  </div>
                  <div className="flex-grow-1">
                    {editingComment && editingComment.id === comment.id ? (
                      <div>
                        <textarea
                          className="form-control mb-2"
                          value={editedContent}
                          onChange={(e) => setEditedContent(e.target.value)}
                        />
                        <button className="btn btn-primary btn-sm me-2" onClick={handleUpdateComment}>Save</button>
                        <button className="btn btn-secondary btn-sm" onClick={() => setEditingComment(null)}>Cancel</button>
                      </div>
                    ) : (
                      <>
                        <p>{comment.content}</p>
                        <div className="d-flex justify-content-between align-items-center">
                          <div>
                            {currentUserProfile && currentUserProfile.username !== comment.user.username && (
                              <button className="btn btn-link btn-sm p-0 me-2" onClick={() => handleLikeComment(comment.id)}>
                                {comment.is_liked_by_user ? 'Unlike' : 'Like'}
                              </button>
                            )}
                            <span className="small text-muted">{comment.likes_count} likes</span>
                          </div>
                          {currentUserProfile && currentUserProfile.username === comment.user.username && (
                            <div>
                              <button className="btn btn-link btn-sm p-0 me-2" onClick={() => handleEditComment(comment)}>Edit</button>
                              <button className="btn btn-link btn-sm p-0 text-danger" onClick={() => handleDeleteComment(comment.id)}>Delete</button>
                            </div>
                          )}
                        </div>
                      </>
                    )}
                  </div>
                </div>
              ))
            ) : (
              <p>No comments yet.</p>
            )}

            <form onSubmit={handleCommentSubmit} className="mt-4">
              <textarea
                className="form-control mb-2"
                value={newComment}
                onChange={(e) => setNewComment(e.target.value)}
                placeholder="Write a comment..."
                required
              />
              <button type="submit" className="btn btn-primary">Post Comment</button>
            </form>
          </>
        ) : (
          <p>Please log in to view and post comments.</p>
        )}
      </div>
      {error && <div className="alert alert-danger mt-3">{error}</div>}
    </div>
  );
}

export default MovieDetail;
