import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import api from '../api/axios';
import { format, parseISO } from 'date-fns';
import { ThumbsUp, MessageCircle } from 'lucide-react';

const DEFAULT_AVATAR = 'https://res.cloudinary.com/dumvsoykz/image/upload/v1724754182/default_profile_yvdjcm.jpg';

function ProfilePage() {
  const [profile, setProfile] = useState(null);
  const [feedItems, setFeedItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isFollowing, setIsFollowing] = useState(false);
  const { username } = useParams();

  useEffect(() => {
    const fetchProfileAndFeed = async () => {
      setLoading(true);
      setError(null);
      try {
        const [profileResponse, feedResponse] = await Promise.all([
          api.get(`profiles/${username}/`),
          api.get(`profiles/${username}/feed/`)
        ]);
        setProfile(profileResponse.data);
        setIsFollowing(profileResponse.data.is_following);
        setFeedItems(feedResponse.data);
      } catch (err) {
        console.error('Error fetching profile or feed:', err);
        setError('Failed to fetch data: ' + (err.response?.data?.detail || err.message));
      } finally {
        setLoading(false);
      }
    };

    fetchProfileAndFeed();
  }, [username]);

  const handleFollowToggle = async () => {
    if (!profile) return;

    try {
      const response = await api.post(`profiles/${profile.id}/follow/`);
      setIsFollowing(!isFollowing);
      setProfile(prev => ({
        ...prev,
        followers_count: isFollowing ? prev.followers_count - 1 : prev.followers_count + 1,
        is_following: !isFollowing
      }));
    } catch (err) {
      console.error('Error toggling follow status:', err);
      setError('Failed to update follow status: ' + (err.response?.data?.detail || err.message));
    }
  };

  const renderFeedItem = (item) => {
    const formattedDate = format(parseISO(item.created_at), "MMM d, yyyy 'at' h:mm a");

    return (
      <li key={`${item.type}-${item.id}`} className="list-group-item d-flex align-items-center py-3">
        <div className="d-flex flex-column align-items-center me-3">
          {item.type === 'comment' ? (
            <MessageCircle size={25} className="me-1" />
          ) : (
            <ThumbsUp size={25} className="me-1" />
          )}
          <span className="small text-muted mt-1">{formattedDate}</span>
        </div>
        <div className="d-flex flex-column ms-3 flex-grow-1">
          <span className="fw-bold">
            {item.type === 'comment' ? 'Commented on' : 'Liked'}
          </span>
          <Link to={`/movie/${item.movie_details?.id}`} className="text-decoration-none">
            <span className="movie-title">{item.movie_details?.title}</span>
          </Link>
        </div>
      </li>
    );
  };

  if (loading) return <div className="text-center p-4">Loading...</div>;
  if (error) return <div className="text-center p-4 text-danger">{error}</div>;
  if (!profile) return <div className="text-center p-4">Profile not found</div>;

  return (
    <div className="container py-4">
      <div className="text-center mb-4">
        <img
          src={profile.avatar || DEFAULT_AVATAR}
          alt={`${profile.username}'s avatar`}
          className="rounded-circle img-fluid mb-3"
          style={{ width: '200px', height: '200px', objectFit: 'cover' }}
        />
        <h1 className="display-4 mb-3">{profile.username}</h1>
        <div className="d-flex justify-content-center align-items-center mb-3">
          <span className="me-3"><strong>{profile.followers_count}</strong> Followers</span>
          <button
            onClick={handleFollowToggle}
            className={`btn ${isFollowing ? 'btn-outline-primary' : 'btn-primary'}`}
          >
            {isFollowing ? 'Unfollow' : 'Follow'}
          </button>
        </div>
        <div className="mb-3">
          <span className="badge bg-secondary fs-6">Karma: {profile.total_likes_received}</span>
        </div>
      </div>

      <div className="mb-4">
        <h2 className="h4 mb-3">Biography</h2>
        <p className="lead">{profile.bio || 'No biography available.'}</p>
      </div>

      <div>
        <h2 className="h4 mb-3">Activity Feed</h2>
        {feedItems.length === 0 ? (
          <p>No activity to display.</p>
        ) : (
          <ul className="list-group">
            {feedItems.map(renderFeedItem)}
          </ul>
        )}
      </div>
    </div>
  );
}

export default ProfilePage;
