import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import api from '../../api/axios';

const DEFAULT_AVATAR = 'https://res.cloudinary.com/dumvsoykz/image/upload/v1724754182/default_profile_yvdjcm.jpg';

function MovieDetail() {
  const [movie, setMovie] = useState(null);
  const [comments, setComments] = useState([]);
  const [error, setError] = useState(null);
  const { id } = useParams();

  useEffect(() => {
    const fetchMovieAndComments = async () => {
      try {
        const [movieResponse, commentsResponse] = await Promise.all([
          api.get(`movies/${id}/`),
          api.get(`movies/${id}/comments/`)
        ]);
        setMovie(movieResponse.data);
        setComments(commentsResponse.data);
      } catch (error) {
        console.error('Error fetching movie details and comments:', error);
        setError('Failed to fetch movie details and comments');
      }
    };

    fetchMovieAndComments();
  }, [id]);

  const handleLike = async () => {
    try {
      const response = await api.post(`likes/toggle_like/`, {
        content_type: 'movie',
        object_id: id
      });
      setMovie(prevMovie => ({
        ...prevMovie,
        likes_count: response.data.likes_count
      }));
    } catch (error) {
      console.error('Error liking movie:', error);
      setError('Failed to like the movie. Please try again.');
    }
  };

  const getAvatarUrl = (publicId) => {
    if (!publicId) return DEFAULT_AVATAR;
    return `https://res.cloudinary.com/dumvsoykz/image/upload/c_thumb,g_face,h_50,w_50/${publicId}`;
  };

  if (error) return <div className="text-red-500 text-center p-4">Error: {error}</div>;
  if (!movie) return <div className="text-center p-4">Loading...</div>;

  return (
    <div className="max-w-4xl mx-auto p-4">
      <div className="movie-detail mb-8">
        <h1 className="text-3xl font-bold mb-4">{movie.title}</h1>
        <img src={movie.thumbnail} alt={movie.title} className="mb-4 rounded-lg shadow-lg" />
        <p className="mb-2">Year: {movie.year}</p>
        <p className="mb-2">Genres: {movie.genres.join(', ')}</p>
        <p className="mb-2">Likes: {movie.likes_count}</p>
        <button 
          onClick={handleLike}
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded mb-4"
        >
          Like
        </button>
        <p className="text-gray-700">{movie.extract}</p>
      </div>

      <div className="comments-section">
        <h2 className="text-2xl font-semibold mb-4">Comments</h2>
        {comments.length > 0 ? (
          <ul className="space-y-4">
            {comments.map((comment) => (
              <li key={comment.id} className="bg-white shadow rounded-lg p-4">
                <div className="flex items-center mb-2">
                  <img
                    src={getAvatarUrl(comment.user.avatar)}
                    alt={`${comment.user.username}'s avatar`}
                    className="w-10 h-10 rounded-full mr-3"
                  />
                  <div>
                    <Link to={`/profile/${comment.user.username}`} className="font-semibold text-blue-600 hover:underline">
                      {comment.user.username}
                    </Link>
                  </div>
                </div>
                <p className="text-gray-700">{comment.content}</p>
                <p className="text-sm text-gray-500 mt-2">
                  Posted on: {new Date(comment.created_at).toLocaleString()}
                </p>
              </li>
            ))}
          </ul>
        ) : (
          <p className="text-gray-500">No comments yet.</p>
        )}
      </div>
    </div>
  );
}

export default MovieDetail;
