import React, { useState, useEffect } from 'react';
import { format } from 'date-fns';

const FeedPage = () => {
  const [feedItems, setFeedItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchFeedItems();
  }, []);

  const fetchFeedItems = async () => {
    try {
      const response = await fetch('/api/feed'); // Adjust this URL to match your API endpoint
      if (!response.ok) {
        throw new Error('Failed to fetch feed items');
      }
      const data = await response.json();
      setFeedItems(data);
      setLoading(false);
    } catch (err) {
      setError(err.message);
      setLoading(false);
    }
  };

  const renderFeedItem = (item) => {
    if (item.type === 'comment') {
      return (
        <div key={item.id} className="bg-white p-4 mb-4 rounded-lg shadow">
          <p className="font-bold">{item.user.username} commented on {item.movie.title}:</p>
          <p className="mt-2">{item.content}</p>
          <p className="text-sm text-gray-500 mt-2">
            {format(new Date(item.created_at), 'PPpp')}
          </p>
        </div>
      );
    } else if (item.type === 'like') {
      return (
        <div key={item.id} className="bg-white p-4 mb-4 rounded-lg shadow">
          <p className="font-bold">{item.user.username} liked {item.movie.title}</p>
          <p className="text-sm text-gray-500 mt-2">
            {format(new Date(item.created_at), 'PPpp')}
          </p>
        </div>
      );
    }
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-6">Feed</h1>
      {feedItems.length === 0 ? (
        <p>No feed items to display.</p>
      ) : (
        feedItems.map(renderFeedItem)
      )}
    </div>
  );
};

export default FeedPage;
