import React, { useState, useEffect, useCallback, useRef } from 'react';
import { Link } from 'react-router-dom';
import api from '../../api/axios';
import './MovieList.css';

function MovieList() {
  const [movies, setMovies] = useState([]);
  const [page, setPage] = useState(1);
  const [loading, setLoading] = useState(false);
  const [hasMore, setHasMore] = useState(true);
  const observer = useRef();

  const fetchMovies = async () => {
    setLoading(true);
    try {
      console.log(`Frontend: Fetching movies for page ${page}...`);
      const response = await api.get('movies/', { 
        params: { page: page },
        headers: { 'Authorization': undefined }  // Explicitly remove Authorization header
      });
      console.log('Frontend: Response:', response.data);
      const newMovies = response.data.results;
      setMovies(prevMovies => [...prevMovies, ...newMovies]);
      setHasMore(response.data.next !== null);
    } catch (error) {
      console.error('Frontend: Error fetching movies:', error);
      if (error.response) {
        console.error('Response data:', error.response.data);
        console.error('Response status:', error.response.status);
        console.error('Response headers:', error.response.headers);
      }
    } finally {
      setLoading(false);
    }
  };

  const handleLike = async (movieId) => {
    try {
      const response = await api.post('likes/toggle_like/', {
        content_type: 'movie',
        object_id: movieId
      }, {
        headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
      });

      setMovies(prevMovies =>
        prevMovies.map(movie =>
          movie.id === movieId
            ? { 
                ...movie, 
                likes_count: response.data.likes_count,
                is_liked_by_user: response.data.is_liked
              }
            : movie
        )
      );
    } catch (error) {
      console.error('Error toggling like for movie:', error);
    }
  };

  const lastMovieElementRef = useCallback(node => {
    if (loading) return;
    if (observer.current) observer.current.disconnect();
    observer.current = new IntersectionObserver(entries => {
      if (entries[0].isIntersecting && hasMore) {
        setPage(prevPage => prevPage + 1);
      }
    });
    if (node) observer.current.observe(node);
  }, [loading, hasMore]);

  useEffect(() => {
    fetchMovies();
  }, [page]);

  return (
    <div>
      <h1>Movies (Page: {page}, Total: {movies.length})</h1>
      <div className="movie-grid">
        {movies.map((movie, index) => (
          <div 
            key={`${movie.id}-${index}`}
            ref={index === movies.length - 1 ? lastMovieElementRef : null}
            className="movie-card"
          >
            <Link to={`/movie/${movie.id}`}>
              <img src={movie.thumbnail} alt={movie.title} />
              <h3>{movie.title}</h3>
            </Link>
            <p>Likes: {movie.likes_count}</p>
            <button onClick={() => handleLike(movie.id)}>
              {movie.is_liked_by_user ? 'Unlike' : 'Like'}
            </button>
          </div>
        ))}
      </div>
      {loading && <p>Loading...</p>}
      {!hasMore && <p>No more movies to load.</p>}
    </div>
  );
}

export default MovieList;
