import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import api from '../../api/axios';
import { useAuth } from '../AuthContext/AuthContext';

function EditProfile() {
  const [profile, setProfile] = useState(null);
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [bio, setBio] = useState('');
  const [avatar, setAvatar] = useState(null);
  const [following, setFollowing] = useState([]);
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const { logout, user } = useAuth();
  const { username: profileUsername } = useParams();


    useEffect(() => {
      fetchProfile();
    }, [profileUsername]);

    const fetchProfile = async () => {
      try {
        const response = await api.get(profileUsername ? `profiles/${profileUsername}/` : 'profiles/me/', {
          headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
        });
        const profileData = response.data;
        setProfile(profileData);
        setUsername(profileData.username);
        setEmail(profileData.email);
        setBio(profileData.bio || '');
        fetchFollowing(profileData.username);  // Fetch following list after profile is loaded
      } catch (error) {
        console.error('Error fetching profile:', error);
        setError('Failed to fetch profile. Please try again.');
      }
    };

    const fetchFollowing = async (username) => {
      const url = `profiles/${username}/following_list/`;
      console.log('Fetching following from URL:', url);
      try {
        const response = await api.get(url);
        console.log('Following data received:', response.data);
        setFollowing(response.data);
      } catch (error) {
        console.error('Error fetching following:', error);
        console.error('Error response:', error.response);
        setError('Failed to fetch following list. Please try again.');
      }
    };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('username', username);
    formData.append('email', email);
    formData.append('bio', bio);
    if (avatar) {
      formData.append('avatar', avatar);
    }

    try {
      const response = await api.put('profiles/me/', formData, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'multipart/form-data',
        },
      });
      console.log('Profile updated successfully:', response.data);
      navigate('/profile');
    } catch (error) {
      console.error('Error updating profile:', error.response?.data || error);
    }
  };

  const handleDeleteAccount = async () => {
    if (window.confirm('Are you sure you want to delete your account? This action cannot be undone.')) {
      try {
        await api.delete('profiles/me/', {
          headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
        });
        logout();
        navigate('/home');
      } catch (error) {
        console.error('Error deleting account:', error);
        setError('Failed to delete account. Please try again.');
      }
    }
  };

  const handleUnfollow = async (profileId) => {
    try {
      await api.post(`profiles/${profileId}/follow/`);
      // After unfollowing, remove the user from the following list
      setFollowing(prevFollowing => prevFollowing.filter(user => user.profile_id !== profileId));
    } catch (error) {
      console.error('Error unfollowing user:', error);
      setError('Failed to unfollow user. Please try again.');
    }
  };

  if (!profile) {
    return <div>Loading...</div>;
  }

  const isOwnProfile = !profileUsername || profileUsername === user.username;

  return (
    <div className="edit-profile">
      <h1>{isOwnProfile ? 'Edit Profile' : `${profile.username}'s Profile`}</h1>
      {error && <div className="error-message">{error}</div>}
      {isOwnProfile && (
        <form onSubmit={handleSubmit}>
          <div>
            <label htmlFor="username">Username:</label>
            <input
              type="text"
              id="username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
          </div>
          <div>
            <label htmlFor="email">Email:</label>
            <input
              type="email"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div>
            <label htmlFor="bio">Bio:</label>
            <textarea
              id="bio"
              value={bio}
              onChange={(e) => setBio(e.target.value)}
            />
          </div>
          <div>
            <label htmlFor="avatar">Avatar:</label>
            <input
              type="file"
              onChange={(e) => setAvatar(e.target.files[0])}
              accept="image/*"
            />
          </div>
          <button type="submit">Update Profile</button>
        </form>
      )}
      <h2>Following</h2>
      <ul>
        {following.map(followedUser => (
          <li key={followedUser.id}>
            {followedUser.username}
            {isOwnProfile && (
              <button onClick={() => handleUnfollow(followedUser.id)}>Unfollow</button>
            )}
          </li>
        ))}
      </ul>
      {isOwnProfile && (
        <button onClick={handleDeleteAccount} className="delete-account">Delete Account</button>
      )}
    </div>
  );
}

export default EditProfile;
