import React from 'react';
import spacer from '../assets/spacer.png';
import jumbotron from '../assets/jumbotron.jpg';

function HomePage() {
    // Mock data for the movie cards
    const movies = [
      { id: 25, title: 'Movie Title 1', thumbnail: 'movie-poster-url-1.jpg', likes_count: 12, comments_count: 5 },
      { id: 80, title: 'Movie Title 2', thumbnail: 'movie-poster-url-2.jpg', likes_count: 25, comments_count: 10 },
      { id: 150, title: 'Movie Title 3', thumbnail: 'movie-poster-url-3.jpg', likes_count: 18, comments_count: 8 },
      { id: 400, title: 'Movie Title 4', thumbnail: 'movie-poster-url-4.jpg', likes_count: 30, comments_count: 15 },
      { id: 450, title: 'Movie Title 5', thumbnail: 'movie-poster-url-5.jpg', likes_count: 22, comments_count: 7 },
    ];
  return (
    <div>
      {/* Jumbotron Section */}
      <div className="jumbotron text-center" style={{
        position: 'relative',
        padding: '150px 0',
        background: `url(${jumbotron}) no-repeat center center / cover` 
        }}>
        <h1 className="display-4 font-weight-bold text-white">
          Discover the Golden Age of Cinema!
        </h1>
        <p className="lead text-white">
          We got it all!
        </p>
      </div>

      {/* Spacer Section */}
      <div className="text-center">
        <img 
          src={spacer} 
          alt="spacer" 
          className="logo" 
          style={{ width: '80%', height: 'auto', maxHeight: '50px' }}
        />
      </div>

      {/* Trending Now Section */}
      <h2 className="text-center mt-4">Trending Now</h2>
      <div className="d-flex justify-content-center flex-wrap">
        {[1, 2, 3, 4, 5].map((number) => (
          <div key={number} className="card m-2" style={{ width: '18rem', position: 'relative' }}>
            <img src={`movie-poster-url-${number}.jpg`} alt={`Movie ${number}`} className="card-img-top" />
            <div className="card-body">
              <h5 className="card-title">Movie Title {number}</h5>
            </div>
            <span className="badge badge-primary" style={{ position: 'absolute', top: '10px', left: '10px' }}>{number}</span>
          </div>
        ))}
      </div>
    </div>
  );
}

export default HomePage;
