import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../api/axios';
import spacer from '../assets/spacer.png';
import jumbotron from '../assets/jumbotron.jpg';
import { ThumbsUp, MessageCircle } from 'lucide-react';

function HomePage() {
  const [trendingMovies, setTrendingMovies] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchTrendingMovies = async () => {
      try {
        const movieIds = [50, 100, 150, 200, 250];
        const movies = await Promise.all(
          movieIds.map(id => api.get(`movies/${id}/`).then(res => res.data))
        );
        setTrendingMovies(movies);
      } catch (error) {
        console.error('Error fetching trending movies:', error);
      }
    };

    fetchTrendingMovies();
  }, []);

  const handleMovieClick = (movieId) => {
    navigate(`/movie/${movieId}`);
  };

  return (
    <div>
      {/* Jumbotron Section */}
      <div className="jumbotron text-center" style={{
        position: 'relative',
        padding: '150px 0',
        background: `url(${jumbotron}) no-repeat center center / cover` 
      }}>
        <h1 className="display-4 font-weight-bold text-white">
          Discover the Golden Age of Cinema!
        </h1>
        <p className="lead text-white">
          We got it all!
        </p>
      </div>

      {/* Spacer Section */}
      <div className="text-center">
        <img 
          src={spacer} 
          alt="spacer" 
          className="logo" 
          style={{ width: '80%', height: 'auto', maxHeight: '50px' }}
        />
      </div>

      {/* Trending now Section */}
      <div style={{ marginTop: '60px' }}>
        <h2 className="text-center mb-4">Trending Now</h2>
        <div className="d-flex justify-content-center">
          <div className="row row-cols-1 row-cols-sm-2 row-cols-md-3 row-cols-lg-5 g-4" style={{ maxWidth: '1200px' }}>
            {trendingMovies.map((movie) => (
              <div key={movie.id} className="col">
                <div className="card h-100" onClick={() => handleMovieClick(movie.id)}>
                  <img src={movie.thumbnail} className="card-img-top" alt={movie.title} style={{ height: '200px', objectFit: 'cover' }} />
                  <div className="card-body">
                    <h5 className="card-title text-center">{movie.title}</h5>
                    <div className="d-flex justify-content-center gap-3">
                      <span className="d-flex align-items-center">
                        <ThumbsUp size={16} className="me-1" />
                        {movie.likes_count}
                      </span>
                      <span className="d-flex align-items-center">
                        <MessageCircle size={16} className="me-1" />
                        {movie.comments_count}
                      </span>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default HomePage;
