import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import api from '../../api/axios';

const DEFAULT_AVATAR = 'https://res.cloudinary.com/dumvsoykz/image/upload/v1724754182/default_profile_yvdjcm.jpg';

function ProfilePage() {
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isFollowing, setIsFollowing] = useState(false);
  const { username } = useParams();

  useEffect(() => {
    const fetchProfile = async () => {
      setLoading(true);
      setError(null);
      try {
        const response = await api.get(`profiles/${username}/`);
        setProfile(response.data);
        setIsFollowing(response.data.is_following);
      } catch (err) {
        console.error('Error fetching profile:', err);
        setError('Failed to fetch profile: ' + (err.response?.data?.detail || err.message));
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, [username]);

  const handleFollowToggle = async () => {
    if (!profile) return;

    try {
      const response = await api.post(`profiles/${profile.id}/follow/`);
      setIsFollowing(!isFollowing);
      setProfile(prev => ({
        ...prev,
        followers_count: isFollowing ? prev.followers_count - 1 : prev.followers_count + 1,
        is_following: !isFollowing
      }));
    } catch (err) {
      console.error('Error toggling follow status:', err);
      setError('Failed to update follow status: ' + (err.response?.data?.detail || err.message));
    }
  };

  if (loading) return <div className="text-center p-4">Loading...</div>;
  if (error) return <div className="text-center p-4 text-red-500">{error}</div>;
  if (!profile) return <div className="text-center p-4">Profile not found</div>;

  return (
    <div className="max-w-4xl mx-auto p-4 flex flex-col md:flex-row">
      <div className="w-full md:w-2/3 bg-white shadow-lg rounded-lg p-6">
        {/* Profile Info */}
        <div className="text-center mb-6">
          <img
            src={profile.avatar || DEFAULT_AVATAR}
            alt={`${profile.username}'s avatar`}
            className="w-24 h-24 rounded-full object-cover mx-auto mb-4"
          />
          <h1 className="text-2xl font-bold text-gray-800">{profile.username}</h1>
        </div>

        {/* Formatted Input Fields */}
        <div className="mb-4">
          <label className="block text-gray-700 font-bold mb-2">Username:</label>
          <input
            type="text"
            value={profile.username}
            disabled
            className="w-full p-2 border border-gray-300 rounded"
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700 font-bold mb-2">Biography:</label>
          <textarea
            value={profile.bio || 'No biography available.'}
            disabled
            className="w-full p-2 border border-gray-300 rounded"
          />
        </div>

        <div className="text-center">
          <button
            onClick={handleFollowToggle}
            className={`mt-4 px-6 py-2 rounded ${
              isFollowing
                ? 'bg-red-500 hover:bg-red-600 text-white'
                : 'bg-blue-500 hover:bg-blue-600 text-white'
            }`}
          >
            {isFollowing ? 'Unfollow' : 'Follow'}
          </button>
        </div>
      </div>

      {/* Following List */}
      <div className="w-full md:w-1/3 bg-white shadow-lg rounded-lg p-6 ml-0 md:ml-6">
        <h2 className="text-xl font-bold mb-4">Following</h2>
        <ul className="space-y-4">
          {profile.following?.map((user) => (
            <li key={user.id} className="flex items-center">
              <img
                src={user.avatar || DEFAULT_AVATAR}
                alt={user.username}
                className="w-10 h-10 rounded-full object-cover mr-4"
              />
              <span>{user.username}</span>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default ProfilePage;
