import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { Container, Row, Col, Image, Button, Form } from 'react-bootstrap';
import api from '../api/axios';
import './ProfilePage.css'; // You'll need to create this CSS file

function ProfilePage() {
  const [profile, setProfile] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [bio, setBio] = useState('');
  const [avatar, setAvatar] = useState(null);
  const [followers, setFollowers] = useState([]);
  const [following, setFollowing] = useState([]);
  const [showFollowers, setShowFollowers] = useState(false);
  const [showFollowing, setShowFollowing] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { username } = useParams();
  const currentUser = JSON.parse(localStorage.getItem('user'));

  useEffect(() => {
    fetchProfile();
  }, [username]);

  const fetchProfile = async () => {
    setLoading(true);
    setError(null);
    try {
      console.log(`Frontend: Fetching profile for ${username}...`);
      const response = await api.get(`profiles/${username}/`);
      console.log('Frontend: Profile Response:', response.data);
      setProfile(response.data);
      setBio(response.data.bio);
    } catch (error) {
      console.error('Frontend: Error fetching profile:', error.response || error);
      setError('Failed to load profile. Please try again later.');
    }
    setLoading(false);
  };

  const handleFollow = async () => {
    try {
      const response = await api.post(`profiles/${profile.id}/follow/`);
      setProfile(prevProfile => ({
        ...prevProfile,
        is_following: response.data.is_following,
        follower_count: response.data.follower_count
      }));
    } catch (error) {
      console.error('Error following/unfollowing:', error.response || error);
    }
  };

  const handleEditProfile = async () => {
    try {
      const formData = new FormData();
      formData.append('bio', bio);
      if (avatar) {
        formData.append('avatar', avatar);
      }

      const response = await api.patch(`profiles/${profile.id}/`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      setProfile(prevProfile => ({
        ...prevProfile,
        bio: response.data.bio,
        avatar: response.data.avatar
      }));
      setIsEditing(false);
    } catch (error) {
      console.error('Error updating profile:', error.response || error);
    }
  };

  const fetchFollowers = async () => {
    try {
      const response = await api.get(`profiles/${profile.id}/followers/`);
      setFollowers(response.data);
      setShowFollowers(true);
    } catch (error) {
      console.error('Error fetching followers:', error.response || error);
    }
  };

  const fetchFollowing = async () => {
    try {
      const response = await api.get(`profiles/${profile.id}/following/`);
      setFollowing(response.data);
      setShowFollowing(true);
    } catch (error) {
      console.error('Error fetching following:', error.response || error);
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;
  if (!profile) return <div>Profile not found</div>;

  return (
    <Container className="profile-container">
      <Row className="mt-4">
        <Col md={4}>
          <Image src={profile.avatar} roundedCircle fluid className="profile-avatar" />
        </Col>
        <Col md={8}>
          <h2>{profile.username}</h2>
          {isEditing ? (
            <Form.Control
              as="textarea"
              value={bio}
              onChange={(e) => setBio(e.target.value)}
              className="bio-edit"
            />
          ) : (
            <p className="bio">{profile.bio}</p>
          )}
          <div className="mt-3">
            <Button variant="link" onClick={fetchFollowers}>
              Followers: {profile.follower_count}
            </Button>
            <Button variant="link" onClick={fetchFollowing}>
              Following: {profile.following_count}
            </Button>
            <span>Total Likes: {profile.total_likes_received}</span>
          </div>
          {currentUser && currentUser.username !== profile.username && (
            <Button variant="primary" onClick={handleFollow} className="mt-2">
              {profile.is_following ? 'Unfollow' : 'Follow'}
            </Button>
          )}
          {currentUser && currentUser.username === profile.username && (
            <>
              {isEditing ? (
                <>
                  <Form.Control
                    type="file"
                    onChange={(e) => setAvatar(e.target.files[0])}
                    className="mt-2"
                  />
                  <Button variant="success" onClick={handleEditProfile} className="mt-2 mr-2">
                    Save Changes
                  </Button>
                  <Button variant="danger" onClick={() => setIsEditing(false)} className="mt-2">
                    Cancel
                  </Button>
                </>
              ) : (
                <Button variant="primary" onClick={() => setIsEditing(true)} className="mt-2">
                  Edit Profile
                </Button>
              )}
            </>
          )}
        </Col>
      </Row>
      {showFollowers && (
        <Row className="mt-4">
          <Col>
            <h3>Followers</h3>
            <ul className="follower-list">
              {followers.map((follower) => (
                <li key={follower.id}>{follower.username}</li>
              ))}
            </ul>
            <Button variant="link" onClick={() => setShowFollowers(false)}>
              Close
            </Button>
          </Col>
        </Row>
      )}
      {showFollowing && (
        <Row className="mt-4">
          <Col>
            <h3>Following</h3>
            <ul className="following-list">
              {following.map((followed) => (
                <li key={followed.id}>{followed.username}</li>
              ))}
            </ul>
            <Button variant="link" onClick={() => setShowFollowing(false)}>
              Close
            </Button>
          </Col>
        </Row>
      )}
    </Container>
  );
}

export default ProfilePage;
