import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import api from '../api/axios';
import { format, parseISO } from 'date-fns';
import { ThumbsUp, MessageCircle } from 'lucide-react';
import defaultAvatar from '../assets/defaultavatar.jpg';

function FeedPage() {
  const [feedItems, setFeedItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchFeedItems = async () => {
      try {
        const response = await api.get('profiles/feed/');
        setFeedItems(response.data);
        setLoading(false);
      } catch (err) {
        console.error('Error details:', err.response || err);
        setError('Failed to fetch feed items: ' + (err.response?.data?.detail || err.message));
        setLoading(false);
      }
    };

    fetchFeedItems();
  }, []);

  const renderFeedItem = (item) => {
    const formattedDate = format(parseISO(item.created_at), "MMM d, yyyy 'at' h:mm a");

    return (
      <div key={`${item.type}-${item.id}`} className="col-12 col-md-6 col-lg-4 mb-4">
        <div className="card h-100 bg-dark text-white">
          <img 
            src={item.movie_details?.thumbnail || '/default-movie-thumbnail.png'} 
            alt={`${item.movie_details?.title} thumbnail`}
            className="card-img-top movie-thumbnail"
          />
          <div className="card-body">
            <h5 className="card-title text-center mb-3">{item.movie_details?.title}</h5>
            <div className="d-flex justify-content-between align-items-start">
              <div className="text-center">
                <Link to={`/profile/${item.user.username}`} className="text-decoration-none">
                  <img 
                    src={item.user.avatar || defaultAvatar} 
                    alt={`${item.user.username}'s avatar`} 
                    className="rounavatar mb-2"
                  />
                  <p className="mb-0">{item.user.username}</p>
                </Link>
                <small className="text-muted">{formattedDate}</small>
              </div>
              <div className="text-center">
                {item.type === 'comment' ? (
                  <MessageCircle size={25} className="mb-2" />
                ) : (
                  <ThumbsUp size={25} className="mb-2" />
                )}
                <p className="mb-0">{item.type === 'comment' ? 'Commented on' : 'Liked'}</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  };

  if (loading) return <div className="text-center p-4">Loading...</div>;
  if (error) return <div className="text-center p-4 text-danger">{error}</div>;

  return (
    <div className="container-fluid py-4 feed-container">
      <h1 className="mb-4 text-center">Feed</h1>
      {feedItems.length === 0 ? (
        <p className="text-center">No feed items to display.</p>
      ) : (
        <div className="row">
          {feedItems.map(renderFeedItem)}
        </div>
      )}
    </div>
  );
}

export default FeedPage;
