import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import api from '../../api/axios';


const DEFAULT_AVATAR = 'https://res.cloudinary.com/dumvsoykz/image/upload/v1724754182/default_profile_yvdjcm.jpg';

function ProfileList() {
  const [profiles, setProfiles] = useState([]);
  const [filteredProfiles, setFilteredProfiles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    const fetchProfiles = async () => {
      try {
        const response = await api.get('profiles/');
        const sortedProfiles = response.data.sort((a, b) => (b.followers_count || 0) - (a.followers_count || 0));
        setProfiles(sortedProfiles);
        setFilteredProfiles(sortedProfiles);
        setLoading(false);
      } catch (err) {
        console.error('Error details:', err.response || err);
        setError('Failed to fetch profiles: ' + (err.response?.data?.detail || err.message));
        setLoading(false);
      }
    };

    fetchProfiles();
  }, []);

  useEffect(() => {
    const results = profiles.filter(profile =>
      profile.username.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredProfiles(results);
  }, [searchTerm, profiles]);

  if (loading) return <div className="text-center p-4">Loading...</div>;
  if (error) return <div className="text-center p-4 text-red-500">{error}</div>;

  return (
    <div className="p-4 profile-list" style={{ backgroundColor: '#181818', color: '#fff' }}>
      <div className="max-w-4xl mx-auto mb-8">
        <h1 className="text-3xl font-bold mb-6 text-center">User Profiles</h1>
        <div className="mb-6 flex justify-center">
          <input
            type="text"
            placeholder="Search profiles..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="w-full max-w-md px-4 py-2 rounded-full bg-gray-700 text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
        </div>
      </div>
      <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 xl:grid-cols-6 gap-4">
        {filteredProfiles.map(profile => (
          <div key={profile.id} className="bg-gray-800 rounded-lg p-3 flex flex-col items-center">
            <img 
              src={profile.avatar || DEFAULT_AVATAR}
              alt={`${profile.username}'s avatar`} 
              className="w-16 h-16 rounded-full object-cover mb-2"
              onError={(e) => {
                console.error(`Error loading avatar for ${profile.username}:`, e);
                e.target.src = DEFAULT_AVATAR;
              }}
            />
            <Link 
              to={`/profile/${profile.username}`}
              className="text-sm font-semibold text-blue-400 hover:underline mb-1 text-center"
            >
              {profile.username}
            </Link>
            <div className="text-xs text-gray-400 text-center">
              <div>{profile.followers_count || 0} followers</div>
              <div>{profile.total_likes_received || 0} likes</div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ProfileList;
